import React, { useState, useEffect } from 'react';

const WeatherDashboard = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [city, setCity] = useState(''); 

  const API_KEY = '7cf7513e537a4eb18e78f7343c7c54ad';

  useEffect(() => {
    
    const fetchWeatherData = async () => {
      try {
        const response = await fetch(
          `https://api.weatherbit.io/v2.0/current?city=${city}&key=${API_KEY}`
        );

        if (!response.ok) {
          throw new Error('City not found');
        }

        const data = await response.json();
        console.log(data);
        setWeatherData(data[data][0]);
      } catch (error) {
        console.error('Error fetching weather data:', error.message);
        setWeatherData(null);
      }
    };

    fetchWeatherData();
  }, [city, API_KEY]);

  return (
    <div>
      <h2>Weather Dashboard</h2>
      <label htmlFor="cityInput">Enter City:</label>
      <input
        type="text"
        id="cityInput"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />

      {weatherData && (
        <div>
          <h3>{weatherData.city_name}, {weatherData.datetime}</h3>
          <p>TimeZone: {weatherData.timezone}</p>
          <p>{weatherData.weather.description}</p>
          <p>Temperature: {weatherData.temp}Â°C</p>
          <p>AQI: {weatherData.aqi}%</p>
          <p>Wind Speed: {weatherData.wind_spd} m/s</p>
          
        </div>
      )}
    </div>
  );
};

export default WeatherDashboard;
