import React, { useState, useEffect } from 'react';
import styles from './styles.module.css';


const WeatherDashboard = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [city, setCity] = useState(''); 
  const API_KEY = '7cf7513e537a4eb18e78f7343c7c54ad';


  useEffect(() => {
    
    const fetchWeatherData = async () => {
      try {
        const response = await fetch(
          `https://api.weatherbit.io/v2.0/current?city=${city}&key=${API_KEY}`
        );

        if (!response.ok) {
          throw new Error('City not found');
        }

        const data = await response.json();
        console.log(data.data[0]);
        setWeatherData(data);
      } catch (error) {
        console.error('Error fetching weather data:', error.message);
        setWeatherData(null);
      }
    };

    fetchWeatherData();
  }, [city, API_KEY]);

  return (
    <div className={styles.container}>
      <h1>Weather Dashboard</h1>
      <label htmlFor="cityInput">Enter City:</label>
      <input
        type="text"
        id="cityInput"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />

      {weatherData && (
        <div className={styles.weatherInfo}>
          <h3>{weatherData.data[0].city_name}, {weatherData.data[0].datetime}</h3>
          <p>TimeZone: {weatherData.data[0].timezone}</p>
          <p>Description: {weatherData.data[0].weather.description}</p>
          <p>Temperature: {weatherData.data[0].temp}Â°C</p>
          <p>AQI: {weatherData.data[0].aqi}%</p>
          <p>Wind Speed: {weatherData.data[0].wind_spd} m/s</p>
        </div>
      )}
    </div>
  );
};

export default WeatherDashboard;
