import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Input from '../../Components/Input';
import Button from '../../Components/Button';

const LoginPage = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const verifyUser = (curr_username,curr_password)=>{

    const auth_obj = [
      {
        "name": "John Doe",
        "username": "john_doe",
        "password": "password123",
        "city": "New York"
      },
      {
        "name": "Jane Smith",
        "username": "jane_smith",
        "password": "pass456",
        "city": "Los Angeles"
      },
      {
        "name": "Alice Johnson",
        "username": "alice_j",
        "password": "secure789",
        "city": "Chicago"
      },
      {
        "name": "Bob Thompson",
        "username": "bob_t",
        "password": "bobpass",
        "city": "San Francisco"
      }
    ]

    auth_obj.forEach(function({username,password}){
      if(curr_username===username && curr_password===password){
        return true;
      }
    })
    return false;
  }

  const handleLogin = () => {

    if(username!="" && password!=""){
      if(verifyUser(username,password)===true){
        alert("yes beta");
        navigate('/home');
      }
    }

  };

  return (
    <div>
      <h2>Login Page</h2>
      <form>
        {/* Input fields and login button */}
        <Input type="text" placeholder="Enter your username" value={username} onChange={(e) => setUsername(e.target.value)} />
        <Input type="password" placeholder="Enter your password" value={password} onChange={(e) => setPassword(e.target.value)} />
        <Button label="Login" onClick={handleLogin} />
      </form>
    </div>
  );
};

export default LoginPage;
